        this.addTodoItem = new EventEmitter();
        this.todoForm = this.fb.group({
            name: ["", Validators.required],
        });
        this.todos = [];
        this.selectedTodoSubscription =
            this.todoEditService.selectedTodo$.subscribe((selectedTodo) => {
                this.selectedTodo = selectedTodo;
                this.todoForm.patchValue({
                    name: selectedTodo ? selectedTodo.name : "",
                });
            });
    }
    ngOnInit() {
        this.todoForm = this.fb.group({
            name: ["", Validators.required],
        });
    }
    // addTodo() {
    //   if (this.todoForm.valid) {
    //     const newTodo = this.todoForm.value;
    //     this.todoForm.reset();
    //     this.addTodoItem.emit(newTodo);
    //   }
    // }
    addTodo() {
        if (this.todoForm.valid) {
            const newTodo = this.todoForm.value;
            if (this.selectedTodo) {
                // Edit existing todo
                const index = this.todos.findIndex((todo) => todo.id === this.selectedTodo.id);
                if (index !== -1) {
                    this.todos[index] = {
                        ...this.selectedTodo,
                        name: newTodo.name, // Update the name property with the new value from the form
                    };
                }
            }
            else {
                // Add new todo
                const generatedId = this.todos.length > 0
                    ? Math.max(...this.todos.map((todo) => todo.id)) + 1
                    : 1;
                const todoToAdd = {
                    id: generatedId,
                    name: newTodo.name,
                    complete: false, // You might want to add a default value for the complete property
                };
                this.todos.push(todoToAdd);
            }
            // Reset the form and clear the selected todo
            this.todoForm.reset();
            this.todoEditService.clearTodo();
        }
    }
