import { Component, EventEmitter, Input, OnInit, Output } from "@angular/core";
import { FormGroup, Validators, FormBuilder } from "@angular/forms";
import { TodoServiceService } from "../todo-service.service";
import { DataShareService } from "../data-share.service";
import { Subscription } from "rxjs";
import { Todo } from "./todo.interface";
@Component({
  selector: "org-new-todos",
  templateUrl: "./new-todos.component.html",
  styleUrls: ["./new-todos.component.scss"],
})
export class NewTodosComponent implements OnInit {
  @Input() selectedTodo: Todo | null = null;
  @Output() addTodoItem = new EventEmitter<any>();
  selectedTodoSubscription: Subscription;
  @Output() todoUpdated = new EventEmitter<Todo>();
  // selectedTodo: any;

  constructor(
    private fb: FormBuilder,
    private todoEditService: DataShareService
  ) {
    this.selectedTodoSubscription =
      this.todoEditService.selectedTodo$.subscribe((selectedTodo) => {
        this.selectedTodo = selectedTodo;
        this.todoForm.patchValue({
          name: selectedTodo ? selectedTodo.name : "",
        });
      });
  }

  todoForm: FormGroup = this.fb.group({
    name: ["", Validators.required],
  });
  todos: any[] = [];

  ngOnInit(): void {
    this.todoForm = this.fb.group({
      name: ["", Validators.required],
    });
  }

  // addTodo() {
  //   if (this.todoForm.valid) {
  //     const newTodo = this.todoForm.value;
  //     this.todoForm.reset();
  //     this.addTodoItem.emit(newTodo);
  //   }
  // }
  addTodo() {
    if (this.todoForm.valid) {
      const newTodo = this.todoForm.value;

      if (this.selectedTodo) {
        // Edit existing todo
        const index = this.todos.findIndex(
          (todo) => todo.id === this.selectedTodo.id
        );
        if (index !== -1) {
          this.todos[index] = {
            ...this.selectedTodo,
            name: newTodo.name, // Update the name property with the new value from the form
          };
        }
      } else {
        const newTodo = this.todoForm.value;
        this.todoForm.reset();
        this.addTodoItem.emit(newTodo);
      }
      this.todoForm.reset();
      this.todoEditService.clearTodo();
    }
  }
  ngOnDestroy() {
    this.selectedTodoSubscription.unsubscribe();
  }
}
<div class="todo">
  <form [formGroup]="todoForm" (ngSubmit)="onSubmit()">
    <input
      formControlName="name"
      type="text"
      placeholder="Create a new todo.."
      id="name"
    />
    <button class="pos-abs" type="submit" [disabled]="todoForm.invalid">
      <img src="../../assets/img/icon-check.svg" alt="" />
    </button>
  </form>
</div>
