module.exports = "<!-- <org-new-todos></org-new-todos> -->\n<div class=\"banner-con\">\n  <div class=\"banner\">\n    <div class=\"content\">\n      <h1>TODO</h1>\n      <img src=\"./img/icon-sun.svg\" alt />\n    </div>\n    <div class=\"todo\">\n      <!-- <org-new-todos (addTodoItem)=\"addTodo($event)\"></org-new-todos> -->\n      <div class=\"todo\">\n        <form [formGroup]=\"todoForm\" (ngSubmit)=\"addTodo()\">\n          <input\n            formControlName=\"name\"\n            type=\"text\"\n            placeholder=\"Create a new todo..\"\n            id=\"name\"\n          />\n          <button class=\"pos-abs\" type=\"submit\" [disabled]=\"todoForm.invalid\">\n            <img src=\"../../assets/img/icon-check.svg\" alt=\"\" />\n          </button>\n        </form>\n      </div>\n      <!-- <org-todo-list (deleteTodoItem)=\"deleteTodo($event)\"></org-todo-list> -->\n      <div class=\"list\">\n        <div class=\"todo-con\">\n          <div *ngFor=\"let todo of getFilteredTodos()\" class=\"todo-li\">\n            <org-loader class=\"mb-5\" *ngIf=\"todo.isLoading\"> </org-loader>\n            <ng-container *ngIf=\"!todo.isLoading\">\n              <span\n                (click)=\"markCompleted(todo)\"\n                [class.active-check]=\"todo.complete\"\n                class=\"check\"\n              >\n                <img src=\"../../assets/img/icon-check.svg\" alt=\"\"\n              /></span>\n              <p [class.completed]=\"todo.complete\">{{ todo.name }}</p>\n              <button (click)=\"deleteTodo(todo.id)\" class=\"close\">\n                <img src=\"../../assets/img/icon-cross.svg\" alt=\"\" />\n              </button>\n            </ng-container>\n          </div>\n        </div>\n      </div>\n      <div class=\"info\">\n        <p class=\"left\">{{ getCountOfTodos() }} items left</p>\n        <div class=\"choice\">\n          <p [class.active]=\"filter === 'all'\" (click)=\"setFilter('all')\">\n            All\n          </p>\n          <p [class.active]=\"filter === 'active'\" (click)=\"setFilter('active')\">\n            Active\n          </p>\n          <p\n            [class.active]=\"filter === 'completed'\"\n            (click)=\"setFilter('completed')\"\n          >\n            Completed\n          </p>\n        </div>\n        <p class=\"clear\" (click)=\"clearCompleted()\">Clear Completed</p>\n      </div>\n    </div>\n  </div>\n</div>\n";

